mapper.select:
SELECT go_prescription.GO_PRESCRIPTION_RESULT_CODE, go_prescription.GO_TREAT_CODE, go_prescription.GO_MEDICINE_CODE, go_prescription.GO_PRESCRIPTION_RESULT_INJECTION_NUMBER, go_prescription.GO_PRESCRIPTION_RESULT_DOSAGE, go_prescription.GO_PRESCRIPTION_RESULT_TOTAL_DATE, go_prescription.GO_PRESCRIPTION_RESULT_USAGE, go_prescription.GO_PRESCRIPTION_RESULT_WRITE_DATE, go_prescription.GO_PRESCRIPTION_RESULT_GO_REGISTRATION_DATE, go_medicine.GO_MEDICINE_NAME, go_hospital.GO_HOSPITAL_NAME, go_hospital.GO_HOSPITAL_ADDRESS, go_citizen.GO_CITIZEN_NAME, go_doctor.GO_DOCTOR_NAME, go_treat_subject.GO_TREAT_SUBJECT_NAME FROM go_prescription
JOIN go_treat ON go_prescription.GO_TREAT_CODE = go_treat.GO_TREAT_CODE JOIN go_hospital ON go_treat.GO_HOSPITAL_ID = go_hospital.GO_HOSPITAL_ID JOIN go_doctor ON go_treat.GO_DOCTOR_ID = go_doctor.GO_DOCTOR_ID JOIN go_citizen ON go_treat.GO_CITIZEN_id = go_citizen.GO_CITIZEN_id JOIN go_treat_subject ON go_doctor.GO_TREAT_SUBJECT_CODE = go_treat_subject.GO_TREAT_SUBJECT_CODE JOIN go_medicine ON go_medicine.GO_MEDICINE_CODE = go_prescription.GO_MEDICINE_CODE
WHERE go_prescription.GO_TREAT_CODE = #{treatCode}
