mapper.select:
SELECT go_surgery_result.GO_SURGERY_RESULT_CODE, go_hospital.GO_HOSPITAL_NAME, go_surgery.GO_SURGERY_NAME, go_doctor.GO_DOCTOR_NAME, go_citizen.GO_CITIZEN_NAME, go_surgery_result.GO_SURGERY_RESULT_GO_REGISTRATION_DATE from go_surgery_result join go_surgery ON go_surgery_result.GO_SURGERY_CODE = go_surgery.GO_SURGERY_CODE join go_treat ON go_surgery_result.GO_TREAT_CODE = go_treat.GO_TREAT_CODE join go_hospital ON go_treat.GO_HOSPITAL_ID = go_hospital.GO_HOSPITAL_ID join go_doctor ON go_treat.GO_DOCTOR_ID = go_doctor.GO_DOCTOR_ID join go_citizen on go_treat.GO_CITIZEN_NO = go_citizen.GO_CITIZEN_NO
GO_SURGERY_RESULT_GO_REGISTRATION_DATE between #{firstDate} and #{secondDate}
AND GO_CITIZEN_ID = #{citizen}
AND GO_HOSPITAL_NAME LIKE #{searchContents}
ORDER BY GO_SURGERY_RESULT_GO_REGISTRATION_DATE DESC

select go_hospitalization.GO_HOSPITALIZATION_CODE, go_hospital.GO_HOSPITAL_NAME, go_hospitalization.GO_HOSPITALIZATION_ENTER_DATE, go_hospitalization.GO_HOSPITALIZATION_EXIT_DATE, go_hospitalization.GO_TREAT_CODE, go_citizen.GO_CITIZEN_NAME from go_hospitalization join go_treat ON go_treat.GO_TREAT_CODE=go_hospitalization.GO_TREAT_CODE join go_hospital ON go_hospital.GO_HOSPITAL_ID = go_treat.GO_HOSPITAL_ID join go_citizen ON go_citizen.GO_CITIZEN_NO = go_treat.GO_CITIZEN_NO
GO_HOSPITALIZATION_EXIT_DATE between #{firstDate} and #{secondDate}
AND GO_CITIZEN_ID = #{citizen}
AND GO_HOSPITAL_NAME LIKE #{searchContents}
ORDER BY GO_HOSPITALIZATION_ENTER_DATE DESC LIMIT 0, 5
